package com.kaustubh.ecommerce;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;public class Enquiry extends AppCompatActivity {    EditText etEnquiryName, etEnquiryContact, etEnquirySociety, etEnquiryAddress;    Button btnEnquirySubmit;    Boolean status = true;    String Category;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_enquiry);        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        getSupportActionBar().setDisplayShowHomeEnabled(true);        Category = getIntent().getStringExtra("Category");        setTitle(Category);        etEnquiryName = (EditText) findViewById(R.id.etEnquiryName);        etEnquiryContact = (EditText) findViewById(R.id.etEnquiryContact);        etEnquirySociety = (EditText) findViewById(R.id.etEnquirySociety);        etEnquiryAddress = (EditText) findViewById(R.id.etEnquiryAddress);        btnEnquirySubmit = (Button) findViewById(R.id.btnEnquirySubmit);        btnEnquirySubmit.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                if (validation()) {                    Toast.makeText(Enquiry.this, "Submit Successful", Toast.LENGTH_SHORT).show();                } else {                    status = true;                }            }        });    }    public boolean validation() {        if (etEnquiryName.getText().toString().length() == 0) {            etEnquiryName.setError("Enter Name");            status = false;        }        if (etEnquirySociety.getText().toString().length() == 0) {            etEnquirySociety.setError("Enter Society");            status = false;        }        if (etEnquiryAddress.getText().toString().length() == 0) {            etEnquiryAddress.setError("Enter Address");            status = false;        }        if (etEnquiryContact.getText().toString().length() != 10) {            etEnquiryContact.setError("Enter valid Mobile Number");            status = false;        }        return status;    }    @Override    public boolean onSupportNavigateUp() {        onBackPressed();        return true;    }}