package com.kaustubh.ecommerce;import android.content.Context;import android.content.Intent;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import com.squareup.picasso.Picasso;import java.util.List;public class MoviesAdapter extends RecyclerView.Adapter<MoviesAdapter.MyViewHolder> {    private List<ProductCategory> moviesList;    Context context;    public class MyViewHolder extends RecyclerView.ViewHolder {        public TextView tvCategoryName;        public ImageView ivCategory;        public LinearLayout card_view;        public MyViewHolder(View view) {            super(view);            tvCategoryName = (TextView) view.findViewById(R.id.tvCategoryName);            ivCategory = (ImageView) view.findViewById(R.id.ivCategory);            card_view = (LinearLayout) view.findViewById(R.id.card_view);        }    }    public MoviesAdapter(List<ProductCategory> moviesList, Context context) {        this.moviesList = moviesList;        this.context = context;    }    @Override    public MyViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View itemView = LayoutInflater.from(parent.getContext())                .inflate(R.layout.category_card_layout, parent, false);        return new MyViewHolder(itemView);    }    @Override    public void onBindViewHolder(MyViewHolder holder, int position) {        final ProductCategory movie = moviesList.get(position);        holder.tvCategoryName.setText(movie.getTitle());        try {            /*String uri = "@drawable/" + movie.getImg();            int imageResource = context.getResources().getIdentifier(uri, null, context.getPackageName());            Drawable res = context.getResources().getDrawable(imageResource);            holder.ivCategory.setImageDrawable(res);*/            Picasso.get().load(commonVariables.imgPath + movie.getImg()).into(holder.ivCategory);        } catch (Exception e) {            e.printStackTrace();        }        holder.card_view.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                if (movie.getSubCategory().equals("No")) {                    Intent i = new Intent(context, Enquiry.class);                    i.putExtra("Category", movie.getTitle());                    i.putExtra("ID", movie.getId());                    context.startActivity(i);                } else {                    Intent i = new Intent(context, SubCategory.class);                    i.putExtra("Category", movie.getTitle());                    context.startActivity(i);                }            }        });    }    @Override    public int getItemCount() {        return moviesList.size();    }}