package com.kaustubh.ecommerce;public class ProductCategory {    private String title, img, id;    public ProductCategory(String id, String name, String img) {        this.id = id;        this.title = name;        this.img = img;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getImg() {        return img;    }    public void setImg(String img) {        this.img = img;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }}