package com.kaustubh.ecommerce;public class ProductCategory {    private String title, img, id, subCategory;    public ProductCategory(String id, String name, String img, String subCategory) {        this.id = id;        this.title = name;        this.img = img;        this.subCategory = subCategory;    }    public String getSubCategory() {        return subCategory;    }    public void setSubCategory(String subCategory) {        this.subCategory = subCategory;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getImg() {        return img;    }    public void setImg(String img) {        this.img = img;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }}