package com.kaustubh.ecommerce;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.DefaultItemAnimator;import android.support.v7.widget.GridLayoutManager;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.widget.TextView;import java.util.ArrayList;import java.util.List;public class SubCategory extends AppCompatActivity {    String Category;    TextView tvCategory;    private List<ProductCategory> movieList = new ArrayList<>();    private RecyclerView rvSubCategoryList;    private MoviesAdapter mAdapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_sub_category);        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        getSupportActionBar().setDisplayShowHomeEnabled(true);        Category = getIntent().getStringExtra("Category");        setTitle(Category);        rvSubCategoryList = (RecyclerView) findViewById(R.id.rvSubCategoryList);        mAdapter = new MoviesAdapter(movieList, this);        RecyclerView.LayoutManager mLayoutManager = new LinearLayoutManager(getApplicationContext());        rvSubCategoryList.setLayoutManager(new GridLayoutManager(this, 2));        rvSubCategoryList.setItemAnimator(new DefaultItemAnimator());        rvSubCategoryList.setAdapter(mAdapter);        prepareMovieData();    }    private void prepareMovieData() {        ProductCategory movie = new ProductCategory("1", "Web Design", "websitedesign", "No");        movieList.add(movie);        movie = new ProductCategory("2", "Online Media Promotion", "promotion", "No");        movieList.add(movie);        movie = new ProductCategory("3", "SEO", "elevator", "No");        movieList.add(movie);        movie = new ProductCategory("4", "Android", "painting", "No");        movieList.add(movie);        movie = new ProductCategory("5", "Computer Repair", "generator", "No");        movieList.add(movie);        mAdapter.notifyDataSetChanged();    }    @Override    public boolean onSupportNavigateUp() {        onBackPressed();        return true;    }}